application.ui._tplCollection["ratepopup.tpl"] = "<div class=\"modalwrap focus\" id=\"RateStars\">              <div class=\"spacer\"> </div>              <div class=\"title white field_@@BrowseView.RatePopup.hideIfNoFlixster()@@\">Help us to recommend other titles you might like by rating this video.</div>              <div class=\"title white field_@@BrowseView.RatePopup.showIfNoFlixster()@@ noflixter\">                <br>Enter rating.              </div>              <br>              <br>              <a down=\"RateButton\" id=\"RateControl\" left=\"RateControl\" right=\"RateControl\" up=\"RateControl\"> </a>              <div action=\"application.events.publish('pointerRate',{'direction':'left'})\" class=\"left-arrowMask\" down=\"RateCancel\" id=\"RatingLeftArrowMask\" pointerfocus=\"BrowseView.RatePopup.setFocusTo('RatingLeftArrow')\" pointermouseout=\"BrowseView.RatePopup.setFocusTo('RatingLeftArrow',true)\"> </div>              <div action=\"application.events.publish('pointerRate',{'direction':'left'})\" class=\"left-arrow\" down=\"RateCancel\" id=\"RatingLeftArrow\"> </div>              <ul id=\"RateHolder\">                <li class=\"star0\"> </li>                <li class=\"star1\"> </li>                <li class=\"star2\"> </li>                <li class=\"star3\"> </li>                <li class=\"star4\"> </li>              </ul>              <div action=\"application.events.publish('pointerRate',{'direction':'right'})\" class=\"right-arrowMask\" down=\"RateButton\" id=\"RatingRightArrowMask\" pointerfocus=\"BrowseView.RatePopup.setFocusTo('RatingRightArrow')\" pointermouseout=\"BrowseView.RatePopup.setFocusTo('RatingRightArrow',true)\"> </div>              <div action=\"application.events.publish('pointerRate',{'direction':'right'})\" class=\"right-arrow\" down=\"RateButton\" id=\"RatingRightArrow\"> </div>              <div class=\"dialog\">Use Left/Right keys on your remote control.</div>              <div class=\"buttons\">                <a action=\"BrowseView.RatePopup.hide()\" class=\"button\" down=\"RateCancel\" id=\"RateCancel\" left=\"RateCancel\" right=\"RateButton\" up=\"RateControl\">Cancel</a>                <a action=\"BrowseView.RatePopup.saveRate()\" class=\"button\" down=\"RateButton\" id=\"RateButton\" left=\"RateCancel\" right=\"RateButton\" up=\"RateControl\">{rate}</a>              </div>            </div>";
